import React from "react";
import Button1 from "../../../components/Button1";
import Button2 from "../../../components/Button2";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowRight } from "@fortawesome/free-solid-svg-icons";
import { stats } from "../../../constants";

const Hero = () => {
  return (
    <section
      id="hero"
      className="w-full flex xl:flex-row flex-col justify-center min-h-screen gap-10 max-container"
    >
      <div className="relative xl:w-2/5 flex flex-col justify-center items-start w-full max-xl:padding-x pt-28">
        <h1
          className="text-8xl font-bold font-palanquin max-sm:text-[72px] max-sm:leading-[82px]
        bg-gradient-to-l from-primary  to-dark-green
        text-transparent bg-clip-text "
        >
          Perfect Balance
        </h1>
        <p className="text-4xl font-bold text-dark-green">
          Balancing Life, Naturally
        </p>
        <p>
          At Perfect Balance, we bring you the finest natural supplements,
          skincare, and wellness essentials to nurture your body, mind, and
          spirit.
        </p>
        <div className="flex justify-start items-center gap-10 ">
          <Button2 label="Explore our products" icon={faArrowRight} />
          <Button1 label="Log in / Sign up" />
        </div>
        <div className="flex justify-start items-start flex-wrap w-full mt-20 gap-16">
          {stats.map((stat) => (
            <div key={stat.label}>
              <p>{stat.label}</p>
              <p>{stat.value}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Hero;
